INSERT INTO `books_book` (`id`, `title`, `author`, `cover`, `isbn`, `description`, `publisher`, `publish_date`, `price`, `pages`, `language`, `created_at`, `updated_at`, `category_id`) VALUES
(1, '红楼梦', '曹雪芹', 'covers/1_红楼梦.jpg', '9787020002207', '《红楼梦》是一部百科全书式的长篇小说，以宝黛爱情悲剧为主线，以四大家族的荣辱兴衰为背景，描绘了18世纪中国封建社会的方方面面。', '人民文学出版社', '2010-01-01', 59.90, 400, '中文', NOW(), NOW(), 1),
(2, '三体', '刘慈欣', 'covers/2_三体.jpg', '9787536692930', '《三体》是刘慈欣创作的系列长篇科幻小说，由《三体》、《黑暗森林》、《死神永生》组成，被称为"中国科幻文学的里程碑"。', '重庆出版社', '2008-01-01', 68.00, 302, '中文', NOW(), NOW(), 2),
(3, '活着', '余华', 'covers/3_活着.jpg', '9787506365437', '《活着》是余华的代表作，讲述了农村人福贵悲惨的人生，乡村人的不幸命运和坚强不屈的精神。', '作家出版社', '2012-08-01', 32.00, 280, '中文', NOW(), NOW(), 3),
(4, '百年孤独', '加西亚·马尔克斯', 'covers/4_百年孤独.jpg', '9787544253994', '《百年孤独》是加西亚·马尔克斯创作的长篇小说，是魔幻现实主义文学的代表作，描写了布恩迪亚家族七代人的传奇故事。', '南海出版公司', '2011-06-01', 55.00, 360, '中文', NOW(), NOW(), 4),
(5, '围城', '钱钟书', 'covers/5_围城.jpg', '9787020049295', '《围城》是钱钟书所著的长篇小说，描写了青年方鸿渐从留学回国后的一系列生活经历，是中国现代文学史上的经典之作。', '人民文学出版社', '2006-07-01', 36.00, 320, '中文', NOW(), NOW(), 3),
(6, '平凡的世界', '路遥', 'covers/6_平凡的世界.jpg', '9787530216781', '《平凡的世界》是中国作家路遥创作的一部全景式地表现中国当代城乡社会生活的百万字长篇小说。', '北京十月文艺出版社', '2017-06-01', 128.00, 1200, '中文', NOW(), NOW(), 3),
(7, '沉默的大多数', '王小波', 'covers/7_沉默的大多数.jpg', '9787530219225', '《沉默的大多数》是王小波的杂文集，收录了他从1992年到1997年之间创作的杂文。', '北京十月文艺出版社', '2018-01-01', 42.00, 300, '中文', NOW(), NOW(), 6),
(8, '白夜行', '东野圭吾', 'covers/8_白夜行.jpg', '9787544258609', '《白夜行》是日本作家东野圭吾的长篇小说代表作，也是其作品中最具影响力的作品之一。', '南海出版公司', '2013-01-01', 59.60, 538, '中文', NOW(), NOW(), 5),
(9, '黑暗森林', '刘慈欣', 'covers/9_黑暗森林.jpg', '9787536693968', '《黑暗森林》是"三体"系列的第二部，讲述了地球文明在三体世界的压迫下的生存挣扎。', '重庆出版社', '2008-05-01', 62.00, 470, '中文', NOW(), NOW(), 2),
(10, '死神永生', '刘慈欣', 'covers/10_死神永生.jpg', '9787536699046', '《死神永生》是"三体"三部曲的最后一部，延续前作壮阔的宇宙想象，描写了人类文明与三体文明的终极决战。', '重庆出版社', '2010-11-01', 64.50, 513, '中文', NOW(), NOW(), 2),
(11, '棋王', '阿城', 'covers/11_棋王.jpg', '9787020069583', '《棋王》是阿城的代表作，通过描写一个棋迷的生活与命运，展示了中国传统文化的魅力。', '人民文学出版社', '2008-03-15', 28.00, 200, '中文', NOW(), NOW(), 3),
(12, '西游记', '吴承恩', 'covers/12_西游记.jpg', '9787020070121', '《西游记》是中国古代第一部浪漫主义长篇神魔小说，描写了唐僧师徒四人西行取经的故事。', '人民文学出版社', '2015-08-20', 45.80, 500, '中文', NOW(), NOW(), 1),
(13, '水浒传', '施耐庵', 'covers/13_水浒传.jpg', '9787020070138', '《水浒传》是中国古代长篇小说，描写了以宋江为首的108位好汉反抗朝廷的故事。', '人民文学出版社', '2016-05-12', 49.90, 600, '中文', NOW(), NOW(), 1),
(14, '解忧杂货店', '东野圭吾', 'covers/14_解忧杂货店.jpg', '9787544270878', '《解忧杂货店》是东野圭吾的奇幻温情小说，讲述了一家可以解决人们烦恼的神奇杂货店的故事。', '南海出版公司', '2014-05-01', 39.50, 320, '中文', NOW(), NOW(), 4),
(15, '嫌疑人X的献身', '东野圭吾', 'covers/15_嫌疑人X的献身.jpg', '9787544258791', '《嫌疑人X的献身》是东野圭吾的代表作，讲述了数学天才石神为了掩护暗恋的邻居靖子杀人案件而周密谋划的故事。', '南海出版公司', '2014-06-15', 35.00, 320, '中文', NOW(), NOW(), 5),
(16, '人类简史', '尤瓦尔·赫拉利', 'covers/16_人类简史.jpg', '9787508647357', '《人类简史》从生物学、人类学、历史学等多学科角度，讲述了人类从远古到现代的进化历程。', '中信出版集团', '2014-11-01', 68.00, 440, '中文', NOW(), NOW(), 7),
(17, '未来简史', '尤瓦尔·赫拉利', 'covers/17_未来简史.jpg', '9787508672069', '《未来简史》是《人类简史》的续篇，探讨了科技发展可能带来的未来人类社会变革。', '中信出版集团', '2017-01-01', 68.00, 400, '中文', NOW(), NOW(), 7),
(18, '云边有个小卖部', '张嘉佳', 'covers/18_云边有个小卖部.jpg', '9787540487645', '《云边有个小卖部》是张嘉佳的长篇小说，讲述了少年刘十三和性格截然不同的女孩米线的成长故事。', '湖南文艺出版社', '2018-06-01', 42.00, 350, '中文', NOW(), NOW(), 3),
(19, '小王子', '圣埃克苏佩里', 'covers/19_小王子.jpg', '9787020073405', '《小王子》是一部经典童话寓言，讲述了小王子从自己星球出发，在各星球旅行中遇见不同的人，最后来到地球的故事。', '人民文学出版社', '2013-01-01', 32.00, 180, '中文', NOW(), NOW(), 4),
(20, '时间简史', '史蒂芬·霍金', 'covers/20_时间简史.jpg', '9787535732309', '《时间简史》是霍金的科普著作，探讨了宇宙的起源、黑洞、时间方向等重要物理学问题。', '湖南科学技术出版社', '2010-04-01', 45.00, 280, '中文', NOW(), NOW(), 8),
(21, '哈利·波特与魔法石', 'J.K.罗琳', 'covers/21_哈利·波特与魔法石.jpg', '9787020033454', '《哈利·波特与魔法石》是"哈利·波特"系列的第一部，讲述了哈利发现自己是巫师并进入霍格沃茨魔法学校的故事。', '人民文学出版社', '2000-09-01', 30.00, 240, '中文', NOW(), NOW(), 9),
(22, '哈利·波特与密室', 'J.K.罗琳', 'covers/22_哈利·波特与密室.jpg', '9787020033461', '《哈利·波特与密室》是"哈利·波特"系列的第二部，讲述了哈利二年级在霍格沃茨的冒险经历。', '人民文学出版社', '2000-10-01', 32.00, 260, '中文', NOW(), NOW(), 9),
(23, '月亮与六便士', '毛姆', 'covers/23_月亮与六便士.jpg', '9787532751693', '《月亮与六便士》是毛姆的代表作，以画家高更的生平为蓝本，讲述了一个追求艺术梦想的中年男子的故事。', '上海译文出版社', '2012-08-01', 39.00, 300, '中文', NOW(), NOW(), 4),
(24, '撒哈拉的故事', '三毛', 'covers/24_撒哈拉的故事.jpg', '9787530211663', '《撒哈拉的故事》是三毛的散文集，记录了她和丈夫荷西在撒哈拉沙漠的生活经历。', '北京十月文艺出版社', '2011-01-01', 39.50, 280, '中文', NOW(), NOW(), 10),
(25, '追风筝的人', '卡勒德·胡赛尼', 'covers/25_追风筝的人.jpg', '9787208061644', '《追风筝的人》讲述了阿富汗少年阿米尔和仆人哈桑之间的故事，探讨了背叛与救赎的主题。', '上海人民出版社', '2006-05-01', 36.00, 320, '中文', NOW(), NOW(), 4),
(26, '明朝那些事儿（全集）', '当年明月', 'covers/26_明朝那些事儿（全集）.jpg', '9787550202825', '《明朝那些事儿》是一部关于明朝历史的通俗读物，以轻松风趣的笔调讲述了明朝276年的历史变迁。', '北京联合出版公司', '2015-01-01', 198.00, 3200, '中文', NOW(), NOW(), 7),
(27, '编程珠玑', 'Jon Bentley', 'covers/27_编程珠玑.jpg', '9787115179289', '《编程珠玑》是计算机科学的经典著作，讲解了程序设计中的算法分析和设计技术。', '人民邮电出版社', '2008-10-01', 49.00, 250, '中文', NOW(), NOW(), 11),
(28, '算法导论', '科曼', 'covers/28_算法导论.jpg', '9787111407010', '《算法导论》是一本全面介绍计算机算法的经典教材，被广泛用于计算机科学教育。', '机械工业出版社', '2012-12-01', 128.00, 780, '中文', NOW(), NOW(), 11),
(29, '蛤蟆先生去看心理医生', '罗伯特·戴博德', 'covers/29_蛤蟆先生去看心理医生.jpg', '9787559635532', '《蛤蟆先生去看心理医生》以寓言故事的形式，介绍了心理治疗的过程和各种心理学理论。', '北京联合出版公司', '2020-03-01', 42.00, 260, '中文', NOW(), NOW(), 12),
(30, '孩子们的诗', '北岛', 'covers/30_孩子们的诗.jpg', '9787521704280', '《孩子们的诗》收录了一批中国儿童原创诗歌作品，展示了天真无邪的童心和奇思妙想。', '中信出版集团', '2019-07-01', 49.80, 200, '中文', NOW(), NOW(), 15),
(31, '枪炮、病菌与钢铁', '贾雷德·戴蒙德', 'covers/31_枪炮、病菌与钢铁.jpg', '9787532776771', '《枪炮、病菌与钢铁》探讨了人类社会发展的不平等性，以及地理、生物和环境因素对人类历史的影响。', '上海译文出版社', '2016-04-01', 65.00, 480, '中文', NOW(), NOW(), 7),
(32, '文学回忆录', '木心', 'covers/32_文学回忆录.jpg', '9787549530625', '《文学回忆录》是木心在纽约讲学的记录，以独特视角解读和评论了世界文学史上的重要作品和作家。', '广西师范大学出版社', '2013-01-01', 98.00, 600, '中文', NOW(), NOW(), 14),
(33, '悲惨世界', '维克多·雨果', 'covers/33_悲惨世界.jpg', '9787020100248', '《悲惨世界》是雨果的代表作，通过冉阿让、珂赛特等人物的故事，展现了19世纪法国社会的景象。', '人民文学出版社', '2014-03-01', 68.00, 800, '中文', NOW(), NOW(), 4),
(34, '我不知道该说什么，关于死亡还是爱情', '普拉斯', 'covers/34_我不知道该说什么，关于死亡还是爱情.jpg', '9787020125166', '这是一本收录了普拉斯生前诗作的诗集，体现了她对生命、死亡和爱情的深刻思考。', '人民文学出版社', '2017-02-01', 42.00, 200, '中文', NOW(), NOW(), 15),
(35, '深入理解计算机系统', 'Randal E. Bryant', 'covers/35_深入理解计算机系统.jpg', '9787111544937', '《深入理解计算机系统》是一本关于计算机系统底层工作原理的经典教材，被誉为"程序员必读"。', '机械工业出版社', '2016-06-01', 139.00, 720, '中文', NOW(), NOW(), 11),
(36, '人间失格', '太宰治', 'covers/36_人间失格.jpg', '9787506380263', '《人间失格》是太宰治的自传体小说，讲述了主人公葉藏从童年到青年的成长经历和心路历程。', '作家出版社', '2015-08-01', 36.00, 240, '中文', NOW(), NOW(), 4),
(37, '拖延心理学', '简·博克', 'covers/37_拖延心理学.jpg', '9787300256861', '《拖延心理学》分析了人们拖延的心理机制，并提供了一系列有效的解决方案。', '中国人民大学出版社', '2018-09-01', 45.00, 280, '中文', NOW(), NOW(), 12),
(38, '金字塔原理', '巴巴拉·明托', 'covers/38_金字塔原理.jpg', '9787544269995', '《金字塔原理》是一本思维方法论著作，教导读者如何构建清晰、有条理、有说服力的思维结构。', '南海出版公司', '2019-01-01', 59.00, 320, '中文', NOW(), NOW(), 13);
